mapper.sql:
SELECT emp_id, emp_name, emp_email, phone_number, job_title, hire_date, join_date
FROM employees


        emp_name LIKE '%' || #{query} || '%'


        AND phone_number LIKE '%' || #{phone_number} || '%'


        AND emp_email LIKE '%' || #{emp_email} || '%'


        AND job_title LIKE '%' || #{job_title} || '%'


      AND hire_date >= #{hire_date_start}


        AND hire_date <= #{hire_date_end}


        AND join_date >= #{join_date_start}


        AND join_date <= #{join_date_end}


ORDER BY emp_id ASC
